package module8;
import static java.lang.Math.*;

public class StaticImport {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double ram = 3456.87;
		double sham = 4565.87;
		
		System.out.println(pow(3,4));
		System.out.println(abs(343.56));
		System.out.println(floor(ram));
		System.out.println(ceil(ram));
		System.out.println(sqrt(4));
		System.out.println(random());
		System.out.println(round(ram));
		System.out.println(max(ram, sham));
		System.out.println(min(ram, sham));
	}

}
